mapper.insert:

insert into train_business.train_carriage (id, train_code, `index`, 
  seat_type, seat_count, `row_count`, 
  col_count, create_time, update_time
  )
values (#{id,jdbcType=BIGINT}, #{trainCode,jdbcType=VARCHAR}, #{index,jdbcType=INTEGER}, 
  #{seatType,jdbcType=CHAR}, #{seatCount,jdbcType=INTEGER}, #{rowCount,jdbcType=INTEGER}, 
  #{colCount,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select

from train_business.train_carriage
where train_code=#{trainCode,jdbcType=VARCHAR} and `index`=#{index,jdbcType=INTEGER}

select

from train_business.train_carriage


    and id=#{id,jdbcType=BIGINT}


    and train_code=#{trainCode,jdbcType=VARCHAR}


    and `index`=#{index,jdbcType=INTEGER}


    and seat_type=#{seatType,jdbcType=CHAR}


    and seat_count=#{seatCount,jdbcType=INTEGER}


    and `row_count`=#{rowCount,jdbcType=INTEGER}


    and col_count=#{colCount,jdbcType=INTEGER}


    and create_time=#{createTime,jdbcType=TIMESTAMP}


    and update_time=#{updateTime,jdbcType=TIMESTAMP}


order by train_code asc, `index` asc
