mapper.select:
select

from train_business.train_seat


    and id=#{id,jdbcType=BIGINT}


    and train_code=#{trainCode,jdbcType=VARCHAR}


    and carriage_index=#{carriageIndex,jdbcType=INTEGER}


    and `row`=#{row,jdbcType=CHAR}


    and col=#{col,jdbcType=CHAR}


    and seat_type=#{seatType,jdbcType=CHAR}


    and carriage_seat_index=#{carriageSeatIndex,jdbcType=INTEGER}


    and create_time=#{createTime,jdbcType=TIMESTAMP}


    and update_time=#{updateTime,jdbcType=TIMESTAMP}


order by train_code asc, carriage_index asc, carriage_seat_index asc
