mapper.select:
select

from train_business.train_station


    and `index`=#{index,jdbcType=INTEGER}


    and train_code=#{trainCode,jdbcType=VARCHAR}

select

from train_business.train_station


    and train_code=#{trainCode,jdbcType=VARCHAR}


    and `name`=#{name,jdbcType=VARCHAR}

select

from train_business.train_station


    and id=#{id,jdbcType=BIGINT}


    and train_code=#{trainCode,jdbcType=VARCHAR}


    and `index`=#{index,jdbcType=INTEGER}


    and `name`=#{name,jdbcType=VARCHAR}


    and name_pinyin=#{namePinyin,jdbcType=VARCHAR}


    and in_time=#{inTime,jdbcType=TIME}


    and out_time=#{outTime,jdbcType=TIME}


    and stop_time=#{stopTime,jdbcType=TIME}


    and km=#{km,jdbcType=DECIMAL}


    and create_time=#{createTime,jdbcType=TIMESTAMP}


    and update_time=#{updateTime,jdbcType=TIMESTAMP}


order by train_code asc, `index` asc
